import React from "react";
import { Spinner } from "react-bootstrap";
import useFetchData from "../Hooks/GetAPI"; // Import the custom hook

function List() {
  const url = "https://api.data.gov/ed/collegescorecard/v1/schools?";

  // Call the custom hook and pass the URL
  const { data: schools, isLoaded, isError } = useFetchData(url);

  if (isError) {
    return <div>Error: {isError.message}</div>;
  }

  return (
    <main className="contanier">
      <h2 className="text-center m-4">{}</h2>
      {!isLoaded && (
        <div className="text-center">
          <Spinner animation="border" role="status" />
          <span>Loading...</span>
        </div>
      )}
      {isLoaded && schools.length === 0 && <div>No schools found.</div>}
<p>sc</p>
      
    </main>
  );
}

export default List;
