import React from "react";

import useFetchData from "../Hooks/GetAPI"; // Import the custom hook

const url = "https://cs464p564-frontend-api.vercel.app/api/countries";
// Call the custom hook and pass the URL

function List() {
  const { data: schools, isLoaded, isError } = useFetchData(url);
  if (isError) {
    return <div>Error: Unable to fetch data!</div>;
  }
  if (isLoaded) {
    return <div>loading ... </div>;
  }
  return (
    <div className=" m-5 text-center">
      <h4>Raghad Page</h4>
      <p>Data has {isLoaded ? "loaded" : "not loaded"}</p>
      <ul>
        {schools.map((itemindex) => (
          // eslint-disable-next-line react/no-array-index-key
          <li key={index}>{school.name}</li> // Assuming the "name" field is in your API data
        ))}
      </ul>
    </div>
  );
}

export default List;
